import React from "react";

export  default class StateComponet extends React.Component{
    constructor(props){
         super(props);
         this.state ={
            count: 20
         }
    }

    inCleik =()=>{
        this.setState({
            count :this.state.count+=1
        })
    }

    render(){
        return (
              <div>
                 <h2>state import </h2>
                 <button onClick={inCleik}>+++</button>
                 <button onClick={inCleik}>----</button>
              </div>
        )
    
    }
}
