import ComponentLife from './ComponentLife';

//用类的形式创建组件
class App extends React.Component{
   
    constructor(props){
       super(props);
       this.state ={
            title :"文本"
       }
    }

    updMount = (date) =>{
       this.setState({
         title :  date
       })
    }

    updMount2 = (date) =>{
        this.updMount('sfdfdsfd');
     }

    //渲染函数
    render(){

        // const aa1 = ["1","2","3"];
        // const aa2 = ["11","22","33"];

        return (<div>
              {/* <h1><input ></input></h1>
