              <MyNav nav = {aa1} />

              <MyNav nav = {aa2}/>
              <Statecomponet /> */}
            <ComponentLife name = {this.state.title}  updMount = {this.updMount}/>
            <button onClick={this.updMount2}>修改</button>

            </div>
            )
    }
}

export  default App;
